import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import ReactGlobe from 'react-globe';
import markers from './markers';
import { Link } from 'react-router-dom' 
import './Explore.css'


// class Explore extends Componenet{ 

//   render() {

//     return (
//       <div> </div>
//     )
//   }
// }

function getTooltipContent(marker) {
  return   (
    <Link to={`/Country/${marker.city}`}>{marker.city}</Link>
  ); //pop up content

}

function Explore() {
  const [event, setEvent] = useState(null);
  const [details, setDetails] = useState(null);
  function onClickMarker(marker, markerObject, event) {
    setEvent({
      type: 'CLICK',
      marker,
      markerObjectID: markerObject.uuid,
      pointerEventPosition: { x: event.clientX, y: event.clientY }, //change to a new pages 
    });
    setDetails(getTooltipContent(marker));
  }
  function onDefocus(previousCoordinates, event) {
    setEvent({
      type: 'DEFOCUS',
      previousCoordinates,
      pointerEventPosition: { x: event.clientX, y: event.clientY },
    });
    setDetails(null);
  } 

  return (
    <div style={{ fontFamily: 'arial', width: '100vw', height: '100vh' }}>
      <Link className='ex-home' to='/'>Home</Link> 
      <ReactGlobe
        markers={markers}
        markerOptions={{
          getTooltipContent,
        }}
        onClickMarker={onClickMarker}
        onDefocus={onDefocus}
      />

      {details && (
        <div 
          style={{ //pop up box
            background: 'white', 
            position: 'absolute',
            fontSize: 20,
            top: 0,
            right: 0,
            padding: 12,
          }}>
          <p>{event.marker && event.marker.city}</p>
          <p>
            EVENT: type={event.type}, position=
            {JSON.stringify(event.pointerEventPosition)})
          </p>

        </div>
      )}
    </div>
  );
}

export default Explore ;
